import React, { Component } from "react";
import Card from "../common/card/card";
import Joi from "joi-browser";
import Form from "../common/form";
import Option from "./optionsComponent";

class QuestionComponent extends Form {
  state = {
    data: { question: "" },
    errors: {}
  };

  schema = {
    question: Joi.string().required()
  };
  onHandleOptionChange = () => {
    this.handleChange();
    const { opinionComponentArray } = this.state;
    let opinionComponentArrayCopy = [...opinionComponentArray];
    opinionComponentArrayCopy.push();
  };
  render() {
    return (
      <Card>
        {this.renderInput("question", "fa fa-quora", "Question")}
        <Option />
      </Card>
    );
  }
}

export default QuestionComponent;
