import React, { Component } from "react";
import Card from "../common/card/card";
import Joi from "joi-browser";
import Form from "../common/form";

class Options extends Form {
  state = {
    data: { option: "" },
    errors: {},
    opinionComponentArray: []
  };
  schema = {
    option: Joi.string().required()
  };
  onHandleOptionChange = () => {
    this.handleChange();
    const {opinionComponentArray} = this.state;
    let opinionComponentArrayCopy = [...opinionComponentArray];
    opinionComponentArrayCopy.push("Component");
    this.setState({opinionComponentArray: opinionComponentArrayCopy})
    console.log()
  }
  render() {
    return (
      <React.Fragment>
        {this.renderOptionsInput("option", "fa fa-telegram", "Option", this.onHandleOptionChange)}
      </React.Fragment>
    );
  }
}

export default Options;
